import React, { useState } from 'react';

function PatientForm() {
  // Inline styles for the form container and elements
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    backgroundImage: 'url(images/Doctro.jpg)', // Add your image path here
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'relative',
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '0%',
    maxWidth: '400px',
    width: '100%',
    padding: '20px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)', // Semi-transparent white background
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    position: 'relative',
    zIndex: '1',
  };

  const overlayStyle = {
    content: '""',
    position: 'absolute',
    top: '0',
    left: '0',
    right: '0',
    bottom: '0',
    backgroundColor: 'rgba(0, 0, 0, 0.4)', // Dark overlay with transparency
    zIndex: '0',
  };

  const inputStyle = {
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '16px',
  };

  const selectStyle = {
    ...inputStyle,
  };

  const buttonStyle = {
    backgroundColor: '#41928e',
    color: 'white',
    border: 'none',
    padding: '10px',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease', // Smooth transition for hover
  };

  const [buttonHover, setButtonHover] = useState(false);

  // Button hover style change
  const hoverStyle = buttonHover ? { backgroundColor: '#0056b3' } : {};

  const handleMouseEnter = () => {
    setButtonHover(true);
  };

  const handleMouseLeave = () => {
    setButtonHover(false);
  };

  return (
    <div style={containerStyle}>
      <div style={overlayStyle}></div>
      <h2 style={{ color: 'linear-gradient(145deg, #19410d, #10caeb)', position: 'relative', zIndex: 1 }}>Patient Registration</h2>
      <form style={formStyle}>
        <label>
          Full Name:
          <input type="text" name="fullName" required style={inputStyle} />
        </label>
        <br />
        <label>
          Email:
          <input type="email" name="email" required style={inputStyle} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" required style={inputStyle} />
        </label>
        <br />
        <label>
          Phone Number:
          <input type="tel" name="phoneNumber" required style={inputStyle} />
        </label>
        <br />
        <label>
          Gender:
          <select name="gender" required style={selectStyle}>
            <option value="">Select</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="transgender">Transgender</option>
          </select>
        </label>
        <br />
        <button
          type="submit"
          style={{ ...buttonStyle, ...hoverStyle }}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Register
        </button>
      </form>
    </div>
  );
}

export default PatientForm;
