    padding: "6px 10px", // Reduced padding for smaller button
    height:"60px",
    width:"90px",
  display:"flex",
  
    border: "none", // No border
    borderRadius: "30px", // Slightly rounded corners for a sleek look
    cursor: "pointer", // Pointer cursor on hover
    fontSize: "11px", // Smaller font size for compactness
    transition: "all 0.2s ease", // Faster transition for smoother interaction
