import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ambulanceData } from './data';
import './AmbulancePage.css';

const AmbulancePage = () => {
  const [selectedAmbulance, setSelectedAmbulance] = useState('');
  const navigate = useNavigate();

  const handleDropdownChange = (e) => {
    setSelectedAmbulance(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();

    const ambulance = ambulanceData.find(
      (a) => a.name.toLowerCase() === selectedAmbulance.toLowerCase()
    );

    if (ambulance) {
      navigate(`/ambulance-details/${ambulance.id}`);
    } else {
      alert('Ambulance service not found. Please select a valid service.');
    }
  };

  return (
    <div className="home-page">
      {/* Hospital Search Section (same as before) */}
      <div className="card-container">
        <div className="card">
          <img
            src="/images/hospital.jpg"
            alt="Search for hospitals"
            className="hospital-img"
          />
          <p>Search for hospitals from our list</p>
        </div>
      </div>

      {/* Hospital Search Section */}
      <div className="search-section">
        <div className="scroll-text">
          <span>Find Hospitals & Doctors</span>
        </div>
        <form className="search-container" onSubmit={handleSearchSubmit}>
          <select
            className="hospital-dropdown"
            value={selectedHospital}
            onChange={handleDropdownChange}
          >
            <option value="" disabled>
              Choose a hospital
            </option>
            {hospitalData.map((hospital) => (
              <option key={hospital.id} value={hospital.name}>
                {hospital.name}
              </option>
            ))}
          </select>
          <button type="submit" className="search-button">
            Search
          </button>
        </form>
      </div>

      {/* Ambulance Section */}
      <div className="ambulance-section">
        {/* Ambulance Card */}
        <div className="card-container">
          <div className="card">
            <img
              src="/images/ambulance.jpg"
              alt="Search for ambulances"
              className="ambulance-img"
            />
            <p>Search for ambulance services from our list</p>
          </div>
        </div>

        {/* Ambulance Search Section */}
        <div className="search-section">
          <form className="search-container" onSubmit={handleSearchSubmit}>
            <select
              className="dropdown"
              value={selectedAmbulance}
              onChange={handleDropdownChange}
            >
              <option value="" disabled>
                Choose an ambulance service
              </option>
              {ambulanceData.map((ambulance) => (
                <option key={ambulance.id} value={ambulance.name}>
                  {ambulance.name}
                </option>
              ))}
            </select>
            <button type="submit" className="search-button">
              Search
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AmbulancePage;
