import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { ambulanceData } from './Ambulancedata';
import './AmbulanceDetailsPage.css';

const AmbulanceDetailsPage = () => {
  const { ambulanceId } = useParams(); // Get the ID from the URL
  const ambulance = ambulanceData.find((a) => a.id === parseInt(ambulanceId)); // Find the ambulance by ID
  const [bookingSuccess, setBookingSuccess] = useState(false); // Alert toggle

  if (!ambulance) {
    return <h1>Ambulance not found!</h1>;
  }

  const handleBookingSubmit = (e) => {
    e.preventDefault();

    // Display the success alert message
    alert("Ambulance service request submitted!");

    // React-based alert state management
    setBookingSuccess(true);
    setTimeout(() => {
      setBookingSuccess(false);
    }, 3000); // Show alert for 3 seconds
  };

  return (
    <div className="ambulance-details-page">
      <h1 className="ambulance-name">{ambulance.name}</h1>

      {/* Ambulance Images */}
      <div className="ambulance-images">
        <img
          src={ambulance.image1}
          alt={`${ambulance.name} view 1`}
          className="ambulance-image"
        />
        <img
          src={ambulance.image2}
          alt={`${ambulance.name} view 2`}
          className="ambulance-image"
        />
      </div>

      {/* Ambulance Info */}
      <p className="ambulance-contact">
        <strong>Contact:</strong> {ambulance.contact}
      </p>
      <p className="ambulance-location">
        <strong>Location:</strong> {ambulance.location}
      </p>

      <h2 className="section-heading">Facilities</h2>
      <ul className="ambulance-facilities">
        <li>24/7 Availability</li>
        <li>Emergency Medical Assistance</li>
        <li>Equipped with Oxygen Cylinders</li>
        <li>First Aid Kits</li>
        <li>Basic and Advanced Life Support</li>
      </ul>

      {/* Booking Form */}
      <h2 className="section-heading">Book Ambulance</h2>
      <form
        className="booking-form"
        onSubmit={handleBookingSubmit}
      >
        <label>
          Name:
          <input type="text" required />
        </label>
        <label>
          Contact:
          <input type="text" required />
        </label>
        <button type="submit">Submit</button>
      </form>

      {/* Success Alert */}
      {bookingSuccess && (
        <div className="alert-popup">Ambulance service request submitted!</div>
      )}
    </div>
  );
};

export default AmbulanceDetailsPage;
