import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { hospitalData } from './data';
import { ambulanceData } from './Ambulancedata';
import './HomePage.css';

const HomePage = () => {
  const [selectedHospital, setSelectedHospital] = useState('');
  const [selectedAmbulance, setSelectedAmbulance] = useState('');
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [emergencyFormData, setEmergencyFormData] = useState({
    name: '',
    contact: '',
    location: '',
    emergencyType: '',
  });
  const navigate = useNavigate();

  const driverName = "John Doe"; // Placeholder driver name
  const driverPhone = "+1234567890"; // Placeholder driver phone number

  const handleHospitalDropdownChange = (e) => {
    setSelectedHospital(e.target.value);
  };

  const handleAmbulanceDropdownChange = (e) => {
    setSelectedAmbulance(e.target.value);
  };

  const handleHospitalSearchSubmit = (e) => {
    e.preventDefault();
    const hospital = hospitalData.find(
      (h) => h.name.toLowerCase() === selectedHospital.toLowerCase()
    );
    if (hospital) {
      navigate(`/details/${hospital.id}`);
    } else {
      alert('Hospital not found. Please select a valid hospital.');
    }
  };

  const handleAmbulanceSearchSubmit = (e) => {
    e.preventDefault();
    const ambulance = ambulanceData.find(
      (a) => a.name.toLowerCase() === selectedAmbulance.toLowerCase()
    );
    if (ambulance) {
      navigate(`/ambulance/details/${ambulance.id}`);
    } else {
      alert('Ambulance not found. Please select a valid ambulance.');
    }
  };

  const toggleEmergencyModal = () => {
    setShowEmergencyModal(!showEmergencyModal);
  };

  const handleEmergencyFormChange = (e) => {
    const { name, value } = e.target;
    setEmergencyFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleEmergencyFormSubmit = (e) => {
    e.preventDefault();

    const { name, contact, location, emergencyType } = emergencyFormData;
    if (!name || !contact || !location || !emergencyType) {
      alert('Please fill in all fields.');
      return;
    }

    // Display alert with all the emergency details
    alert(
      `Emergency Alert:
      Driver Status: The driver is on the way.
      Driver Name: ${driverName}
      Driver Phone: ${driverPhone}
      Your Location: ${location}
      Emergency Type: ${emergencyType}`
    );

    // Close the modal
    toggleEmergencyModal();

    // Clear form data
    setEmergencyFormData({
      name: '',
      contact: '',
      location: '',
      emergencyType: '',
    });
  };

  return (
    <div className="home-page">
      {/* Emergency Button */}
      <button className="emergency-button" onClick={toggleEmergencyModal}>
        Emergency
      </button>

      {/* Emergency Modal */}
      {showEmergencyModal && (
        <div className="emergency-modal">
          <div className="modal-content">
            <h2>Emergency Contact Form</h2>
            <form className="emergency-form" onSubmit={handleEmergencyFormSubmit}>
              <label htmlFor="name">Full Name:</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Enter your name"
                value={emergencyFormData.name}
                onChange={handleEmergencyFormChange}
                required
              />

              <label htmlFor="contact">Contact Number:</label>
              <input
                type="tel"
                id="contact"
                name="contact"
                placeholder="Enter your contact number"
                value={emergencyFormData.contact}
                onChange={handleEmergencyFormChange}
                required
              />

              <label htmlFor="location">Location:</label>
              <input
                type="text"
                id="location"
                name="location"
                placeholder="Enter your location"
                value={emergencyFormData.location}
                onChange={handleEmergencyFormChange}
                required
              />

              <label htmlFor="emergency-type">Emergency Type:</label>
              <select
                id="emergency-type"
                name="emergencyType"
                value={emergencyFormData.emergencyType}
                onChange={handleEmergencyFormChange}
                required
              >
                <option value="">Select Emergency Type</option>
                <option value="accident">Accident</option>
                <option value="medical">Medical</option>
                <option value="fire">Fire</option>
                <option value="other">Other</option>
              </select>

              <button type="submit" className="submit-button">
                Submit
              </button>
            </form>
            <button className="close-button" onClick={toggleEmergencyModal}>
              Close
            </button>
          </div>
        </div>
      )}

      {/* The rest of your homepage (hospital and ambulance search) */}
      {/* Hospital and Ambulance sections remain unchanged */}
    </div>
  );
};

export default HomePage;








