import React from "react";
import { useParams } from "react-router-dom";
import { hospitals } from "./data"; // Import hospital data
import "./DetailsPage.css"; // Import CSS

const DetailsPage = () => {
  const { id } = useParams();
  const hospital = hospitals.find((hospital) => hospital.id === parseInt(id));

  if (!hospital) {
    return <p>Hospital not found!</p>;
  }

  return (
    <div className="details-page">
      <div className="hospital-header">
        <img
          src={hospital.images[0]} // Display first image as main image
          alt={hospital.name}
          className="hospital-header-img"
        />
        <div className="hospital-info">
          <h1>{hospital.name}</h1>
          <p>{hospital.introduction}</p>
          <p><strong>Address:</strong> {hospital.address}</p>
        </div>
      </div>

      <div className="doctor-section">
        <h2>Doctors</h2>
        {hospital.doctors.map((doctor) => (
          <div className="doctor-card" key={doctor.id}>
            <img
              src={doctor.photo}
              alt={doctor.name}
              className="doctor-img"
            />
            <div className="doctor-info">
              <h3>{doctor.name}</h3>
              <p><strong>Profession:</strong> {doctor.profession}</p>
              <p><strong>Experience:</strong> {doctor.experience}</p>
              <p><strong>Contact:</strong> {doctor.mobile}</p>
              <p>{doctor.shortIntro}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="appointment-form">
        <h2>Book Appointment</h2>
        <form>
          <label htmlFor="name">Full Name:</label>
          <input type="text" id="name" required />

          <label htmlFor="email">Email:</label>
          <input type="email" id="email" required />

          <label htmlFor="date">Appointment Date:</label>
          <input type="date" id="date" required />

          <button type="submit" className="submit-btn">
            Book Appointment
          </button>
        </form>
      </div>
    </div>
  );
};

export default DetailsPage;
