import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from React Router


const Header = () => {
  const [showOptions, setShowOptions] = useState(false); // State to toggle buttons
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login state

  const handleLoginClick = () => {
    if (isLoggedIn) {
      setIsLoggedIn(false); // If logged in, show sign-up text
    } else {
      setShowOptions(!showOptions); // Toggle Doctor/Patient buttons on login click
      setIsLoggedIn(true); // Mark user as logged in
    }
  };

  return (
    <header>
      <div className="logo">
        <img src="images/Doctro2.jpg" alt="Doctro Logo" className="logo" />
        {/* Replace '/path/to/your/logo.png' with the actual path of your logo image */}
      </div>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link> {/* Link to Home */}
          </li>
          <li>
            <Link to="/about">About</Link> {/* Link to About */}
          </li>
          <li>
            <Link to="/services">Our Services</Link> {/* Link to Our Services */}
          </li>
          <li>
            <button className="login-btn" onClick={handleLoginClick}>
              {isLoggedIn ? 'Sign Up' : 'Login'} {/* Toggle between Login and Sign Up */}
            </button>
          </li>
          {showOptions && (
            <>
              <li>
                <button className="doctor-btn">Doctor</button>
              </li>
              <li>
                <button className="patient-btn">Patient</button>
              </li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
