import React from "react";
import { useParams, useNavigate } from "react-router-dom";

// Mock data for disease details
const diseaseDetails = [
  {
    id: 1,
    name: "Diabetes",
    description:
      "Diabetes is a chronic condition that affects how your body processes blood sugar (glucose). Common treatments include insulin therapy, medication, and lifestyle changes.",
    treatments: [
      "Insulin therapy",
      "Oral medication",
      "Dietary modifications",
      "Regular exercise",
    ],
    doctors: [
      { name: "Dr. John Doe", experience: 10, fee: 500 },
      { name: "Dr. Sarah Smith", experience: 8, fee: 400 },
      { name: "Dr. Emily Johnson", experience: 12, fee: 600 },
    ],
  },
  {
    id: 2,
    name: "Hypertension",
    description:
      "Hypertension, or high blood pressure, increases the risk of heart disease. Treatment includes medication, diet changes, and regular exercise.",
    treatments: [
      "Medication",
      "Low-sodium diet",
      "Weight management",
      "Regular physical activity",
    ],
    doctors: [
      { name: "Dr. Alice Brown", experience: 9, fee: 450 },
      { name: "Dr. Mark Wilson", experience: 11, fee: 550 },
      { name: "Dr. Sophia Miller", experience: 7, fee: 400 },
    ],
  },
  {
    id: 3,
    name: "Asthma",
    description:
      "Asthma is a condition in which your airways narrow and swell, causing breathing difficulties. Common treatments include inhalers, medication, and avoiding triggers.",
    treatments: [
      "Inhaler therapy",
      "Bronchodilators",
      "Allergy medications",
      "Lifestyle modifications",
    ],
    doctors: [
      { name: "Dr. Richard Lee", experience: 10, fee: 450 },
      { name: "Dr. Jennifer Clark", experience: 8, fee: 500 },
      { name: "Dr. David Moore", experience: 12, fee: 600 },
    ],
  },
  {
    id: 4,
    name: "Heart Disease",
    description:
      "Heart disease refers to various types of heart conditions, including coronary artery disease, arrhythmias, and heart failure. Treatment involves lifestyle changes, medications, and surgical procedures.",
    treatments: [
      "Medications for blood pressure and cholesterol",
      "Surgical procedures",
      "Lifestyle modifications",
      "Cardiac rehabilitation",
    ],
    doctors: [
      { name: "Dr. Patricia King", experience: 15, fee: 700 },
      { name: "Dr. Michael Harris", experience: 10, fee: 650 },
      { name: "Dr. Angela Lewis", experience: 12, fee: 750 },
    ],
  },
  {
    id: 5,
    name: "Arthritis",
    description:
      "Arthritis causes inflammation and stiffness in the joints, often leading to pain and mobility issues. Treatment includes medications, physical therapy, and lifestyle changes.",
    treatments: [
      "Anti-inflammatory medications",
      "Physical therapy",
      "Joint injections",
      "Weight management",
    ],
    doctors: [
      { name: "Dr. Carol Adams", experience: 14, fee: 500 },
      { name: "Dr. Steven Scott", experience: 9, fee: 550 },
      { name: "Dr. Maria White", experience: 11, fee: 600 },
    ],
  },
  {
    id: 6,
    name: "Migraine",
    description:
      "Migraine is a neurological condition that causes severe headaches and other symptoms such as nausea and sensitivity to light. Treatment involves medication and lifestyle adjustments.",
    treatments: [
      "Pain relief medication",
      "Preventive medications",
      "Stress management techniques",
      "Dietary modifications",
    ],
    doctors: [
      { name: "Dr. Brian Martinez", experience: 8, fee: 450 },
      { name: "Dr. Laura Walker", experience: 10, fee: 500 },
      { name: "Dr. Kevin Young", experience: 12, fee: 550 },
    ],
  },
];

const DiseaseDetail = () => {
  const { id } = useParams(); // Fetching the disease id from the URL
  const navigate = useNavigate();
  const disease = diseaseDetails.find((d) => d.id === parseInt(id));

  if (!disease) {
    return (
      <div className="text-center mt-20">
        <h1 className="text-2xl font-bold">Disease not found</h1>
        <button
          onClick={() => navigate(-1)}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
        >
          Go Back
        </button>
      </div>
    );
  }

  const handleBooking = () => {
    const appointmentNumber = Math.floor(1000 + Math.random() * 9000); // Generate random 4-digit appointment number
    alert(
      `Appointment booked successfully! Your appointment number is ${appointmentNumber}.`
    );
  };

  return (
    <div className="p-6">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 bg-gray-200 px-4 py-2 rounded"
      >
        ← Back
      </button>
      <h1 className="text-3xl font-bold mb-4">{disease.name}</h1>
      <p className="text-lg mb-6">{disease.description}</p>
      <h2 className="text-2xl font-semibold mb-3">Treatment Options:</h2>
      <ul className="list-disc list-inside mb-6">
        {disease.treatments.map((treatment, index) => (
          <li key={index}>{treatment}</li>
        ))}
      </ul>
      <h2 className="text-2xl font-semibold mb-3">Available Doctors:</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-6">
        {disease.doctors.map((doctor, index) => (
          <div
            key={index}
            className="p-4 border rounded-lg shadow hover:shadow-lg"
          >
            <h3 className="text-xl font-bold">{doctor.name}</h3>
            <p>Experience: {doctor.experience} years</p>
            <p>Consultation Fee: ₹{doctor.fee}</p>
          </div>
        ))}
      </div>
      <button
        onClick={handleBooking}
        className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
      >
        Book Appointment
      </button>
    </div>
  );
};

export default DiseaseDetail;
