// HomePage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { hospitalData } from './data';

const HomePage = () => {
  const [search, setSearch] = useState('');

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const filteredHospitals = hospitalData.filter(hospital =>
    hospital.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="home-page">
      <div className="search-section">
        <h1>Find Hospitals & Doctors</h1>
        <div className="search-container">
          <select className="search-bar" value={search} onChange={handleSearchChange}>
            <option value="">Select Hospital</option>
            {hospitalData.map((hospital) => (
              <option key={hospital.id} value={hospital.name}>
                {hospital.name}
              </option>
            ))}
          </select>
          <button className="search-button">Search</button>
        </div>
      </div>
      
      <div className="card-container">
        {filteredHospitals.map((hospital) => (
          <div key={hospital.id} className="card">
            <img src={hospital.image} alt={hospital.name} />
            <h2>{hospital.name}</h2>
            <p>{hospital.address}</p>
            <Link to={`/details/${hospital.id}`} className="card-button">View Details</Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
