import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

// Mock data for disease details (6 diseases)
const diseaseDetails = [
  {
    id: 1,
    name: "Diabetes",
    description:
      "Diabetes is a chronic condition that affects how your body processes blood sugar (glucose). Common treatments include insulin therapy, medication, and lifestyle changes.",
    treatments: [
      "Insulin therapy",
      "Oral medication",
      "Dietary modifications",
      "Regular exercise",
    ],
    doctors: [
      { name: "Dr. John Doe", experience: 10, fee: 500 },
      { name: "Dr. Sarah Smith", experience: 8, fee: 400 },
      { name: "Dr. Emily Johnson", experience: 12, fee: 600 },
    ],
  },
  {
    id: 2,
    name: "Hypertension",
    description:
      "Hypertension (High Blood Pressure) is a condition where the force of the blood against the artery walls is too high, which can cause health issues like heart disease.",
    treatments: [
      "Medication",
      "Regular exercise",
      "Low salt diet",
      "Stress management",
    ],
    doctors: [
      { name: "Dr. Mark Lee", experience: 15, fee: 700 },
      { name: "Dr. Rachel Green", experience: 9, fee: 450 },
      { name: "Dr. James Smith", experience: 13, fee: 550 },
    ],
  },
  {
    id: 3,
    name: "Asthma",
    description:
      "Asthma is a respiratory condition that causes difficulty breathing due to inflammation and narrowing of the airways. It can be controlled with medication and avoiding triggers.",
    treatments: [
      "Inhalers",
      "Allergy medication",
      "Avoidance of triggers",
      "Breathing exercises",
    ],
    doctors: [
      { name: "Dr. Laura White", experience: 12, fee: 600 },
      { name: "Dr. Michael Brown", experience: 10, fee: 500 },
      { name: "Dr. Karen Williams", experience: 7, fee: 450 },
    ],
  },
  {
    id: 4,
    name: "Cancer",
    description:
      "Cancer is a disease caused by abnormal cell growth. Treatments vary depending on the type, stage, and location of cancer.",
    treatments: [
      "Chemotherapy",
      "Radiation therapy",
      "Surgery",
      "Immunotherapy",
    ],
    doctors: [
      { name: "Dr. Alice King", experience: 20, fee: 1000 },
      { name: "Dr. Robert Scott", experience: 18, fee: 950 },
      { name: "Dr. Samuel Moore", experience: 15, fee: 900 },
    ],
  },
  {
    id: 5,
    name: "Pneumonia",
    description:
      "Pneumonia is an infection that inflames the air sacs in one or both lungs, causing symptoms like coughing, fever, and difficulty breathing.",
    treatments: [
      "Antibiotics",
      "Rest",
      "Oxygen therapy",
      "Hydration",
    ],
    doctors: [
      { name: "Dr. Daniel Clark", experience: 10, fee: 500 },
      { name: "Dr. Linda Harris", experience: 11, fee: 550 },
      { name: "Dr. Sophia Davis", experience: 9, fee: 475 },
    ],
  },
  {
    id: 6,
    name: "Chronic Kidney Disease",
    description:
      "Chronic kidney disease is a gradual loss of kidney function, often caused by diabetes or high blood pressure. Treatment focuses on managing the condition and preventing further damage.",
    treatments: [
      "Dialysis",
      "Blood pressure management",
      "Dietary changes",
      "Kidney transplant (in severe cases)",
    ],
    doctors: [
      { name: "Dr. David Carter", experience: 14, fee: 750 },
      { name: "Dr. Emily Martinez", experience: 13, fee: 700 },
      { name: "Dr. John Williams", experience: 12, fee: 650 },
    ],
  },
];

const DiseaseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const disease = diseaseDetails.find((d) => d.id === parseInt(id));

  if (!disease) {
    return (
      <div className="text-center mt-20">
        <h1 className="text-2xl font-bold">Disease not found</h1>
        <button
          onClick={() => navigate(-1)}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
        >
          Go Back
        </button>
      </div>
    );
  }

  // State for form and booking message
  const [showForm, setShowForm] = useState(false);
  const [bookingMessage, setBookingMessage] = useState("");

  const handleBooking = () => {
    setShowForm(true); // Show the form when the button is clicked
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const name = formData.get("name");
    const age = formData.get("age");
    const sex = formData.get("sex");
    const phone = formData.get("phone");
    const address = formData.get("address");

    // Log patient details
    console.log("Patient Details:", { name, age, sex, phone, address });

    // Generate appointment number
    const appointmentNumber = Math.floor(1000 + Math.random() * 9000);

    // Update booking message
    setBookingMessage(
      `Appointment booked successfully! Your appointment number is ${appointmentNumber}.`
    );

    setShowForm(false); // Hide the form after submission
  };

  return (
    <div className="p-6">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 bg-gray-200 px-4 py-2 rounded"
      >
        ← Back
      </button>
      <h1 className="text-3xl font-bold mb-4">{disease.name}</h1>
      <p className="text-lg mb-6">{disease.description}</p>
      <h2 className="text-2xl font-semibold mb-3">Treatment Options:</h2>
      <ul className="list-disc list-inside mb-6">
        {disease.treatments.map((treatment, index) => (
          <li key={index}>{treatment}</li>
        ))}
      </ul>
      <h2 className="text-2xl font-semibold mb-3">Available Doctors:</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-6">
        {disease.doctors.map((doctor, index) => (
          <div key={index} className="p-4 border rounded-lg shadow hover:shadow-lg">
            <h3 className="text-xl font-bold">{doctor.name}</h3>
            <p>Experience: {doctor.experience} years</p>
            <p>Consultation Fee: ₹{doctor.fee}</p>
          </div>
        ))}
      </div>

      <button
        onClick={handleBooking}
        className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
      >
        Book Appointment
      </button>

      {/* If the form is visible, show it */}
      {showForm && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold">Enter Your Details</h2>
          <form onSubmit={handleFormSubmit} className="mt-4">
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              className="w-full p-2 border rounded mb-3"
              required
            />
            <input
              type="number"
              name="age"
              placeholder="Age"
              className="w-full p-2 border rounded mb-3"
              required
            />
            <select
              name="sex"
              className="w-full p-2 border rounded mb-3"
              required
            >
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Transgender">Transgender</option>
            </select>
            <input
              type="text"
              name="phone"
              placeholder="Phone Number"
              className="w-full p-2 border rounded mb-3"
              required
            />
            <textarea
              name="address"
              placeholder="Address"
              className="w-full p-2 border rounded mb-3"
              required
            ></textarea>
            <button
              type="submit"
              className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
            >
              Submit
            </button>
          </form>
        </div>
      )}

      {/* Display booking message if available */}
      {bookingMessage && (
        <div className="mt-4 p-4 bg-green-100 text-green-800 rounded">
          {bookingMessage}
        </div>
      )}
    </div>
  );
};

export default DiseaseDetail;
