import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

// Mock data for disease details
const diseaseDetails = [
  {
    id: 1,
    name: "Diabetes",
    description:
      "Diabetes is a chronic condition that affects how your body processes blood sugar (glucose). Common treatments include insulin therapy, medication, and lifestyle changes.",
    treatments: [
      "Insulin therapy",
      "Oral medication",
      "Dietary modifications",
      "Regular exercise",
    ],
    doctors: [
      { name: "Dr. John Doe", experience: 10, fee: 500 },
      { name: "Dr. Sarah Smith", experience: 8, fee: 400 },
      { name: "Dr. Emily Johnson", experience: 12, fee: 600 },
    ],
  },
  {
    id: 2,
    name: "Asthma",
    description:
      "Asthma is a condition in which your airways narrow and swell, making it difficult to breathe. Treatments often include inhalers, steroids, and bronchodilators.",
    treatments: [
      "Inhalers",
      "Steroid therapy",
      "Bronchodilators",
      "Lifestyle changes",
    ],
    doctors: [
      { name: "Dr. Alice White", experience: 15, fee: 550 },
      { name: "Dr. Bob Green", experience: 6, fee: 450 },
      { name: "Dr. Chris Black", experience: 9, fee: 500 },
    ],
  },
  // Add more diseases as needed
  {
    id: 3,
    name: "Hypertension",
    description:
      "Hypertension, also known as high blood pressure, can cause severe complications if left untreated. Management includes medications, dietary changes, and exercise.",
    treatments: [
      "Blood pressure medication",
      "Dietary modifications",
      "Regular physical activity",
    ],
    doctors: [
      { name: "Dr. Karen Blue", experience: 7, fee: 400 },
      { name: "Dr. Nathan Red", experience: 13, fee: 550 },
      { name: "Dr. Emma Gray", experience: 11, fee: 600 },
    ],
  },
  {
    id: 4,
    name: "Cancer",
    description:
      "Cancer is a group of diseases involving abnormal cell growth. Treatment options vary depending on the type of cancer, including chemotherapy, radiation, and surgery.",
    treatments: [
      "Chemotherapy",
      "Radiation therapy",
      "Surgery",
      "Immunotherapy",
    ],
    doctors: [
      { name: "Dr. Olivia White", experience: 15, fee: 800 },
      { name: "Dr. Liam Brown", experience: 10, fee: 750 },
      { name: "Dr. Sophia Purple", experience: 20, fee: 900 },
    ],
  },
  {
    id: 5,
    name: "Pneumonia",
    description:
      "Pneumonia is an infection that inflames the air sacs in one or both lungs. Treatment involves antibiotics, antivirals, or antifungals depending on the type of pneumonia.",
    treatments: [
      "Antibiotics",
      "Antivirals",
      "Oxygen therapy",
      "Rest and fluids",
    ],
    doctors: [
      { name: "Dr. Jack Gold", experience: 18, fee: 600 },
      { name: "Dr. Grace Silver", experience: 10, fee: 500 },
      { name: "Dr. Lucy Copper", experience: 12, fee: 650 },
    ],
  },
  {
    id: 6,
    name: "Migraine",
    description:
      "Migraines are severe headaches that can cause nausea, vomiting, and sensitivity to light. Treatments often include pain relief medications, lifestyle changes, and preventive treatments.",
    treatments: [
      "Pain relief medications",
      "Lifestyle changes",
      "Preventive treatments",
    ],
    doctors: [
      { name: "Dr. Kate Violet", experience: 9, fee: 450 },
      { name: "Dr. Daniel Pink", experience: 8, fee: 400 },
      { name: "Dr. Natalie Green", experience: 14, fee: 500 },
    ],
  },
];


const DiseaseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const disease = diseaseDetails.find((d) => d.id === parseInt(id));

  const [showForm, setShowForm] = useState(false);
  const [bookingMessage, setBookingMessage] = useState("");

  if (!disease) {
    return (
      <div style={styles.notFound}>
        <h1>Disease not found</h1>
        <motion.button
          style={styles.backButton}
          onClick={() => navigate(-1)}
          whileHover={{ scale: 1.1 }}
        >
          Go Back
        </motion.button>
      </div>
    );
  }

  const handleBooking = () => {
    setShowForm(true);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const appointmentNumber = Math.floor(1000 + Math.random() * 9000);
    setBookingMessage(
      `Appointment booked successfully! Your appointment number is ${appointmentNumber}.`
    );
    setShowForm(false);
  };

  return (
    <motion.div
      style={styles.container}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.7 }}
    >
      <motion.button
        style={styles.backButton}
        onClick={() => navigate(-1)}
        whileHover={{ scale: 1.1 }}
      >
        ← Back
      </motion.button>
      <motion.h1
        style={styles.title}
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.7 }}
      >
        {disease.name}
      </motion.h1>
      <motion.p
        style={styles.description}
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.7 }}
      >
        {disease.description}
      </motion.p>
      <motion.h2
        style={styles.subHeading}
        initial={{ x: -50, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.7 }}
      >
        Treatment Options:
      </motion.h2>
      <ul style={styles.treatmentList}>
        {disease.treatments.map((treatment, index) => (
          <motion.li
            key={index}
            style={styles.listItem}
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: index * 0.2, duration: 0.5 }}
          >
            {treatment}
          </motion.li>
        ))}
      </ul>
      <motion.h2
        style={styles.subHeading}
        initial={{ x: -50, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.7 }}
      >
        Available Doctors:
      </motion.h2>
      <div style={styles.doctorContainer}>
        {disease.doctors.map((doctor, index) => (
          <motion.div
            key={index}
            style={styles.doctorCard}
            whileHover={{ scale: 1.05 }}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: index * 0.2 }}
          >
            <h3 style={styles.doctorName}>{doctor.name}</h3>
            <p>Experience: {doctor.experience} years</p>
            <p>Consultation Fee: ₹{doctor.fee}</p>
          </motion.div>
        ))}
      </div>
      <motion.button
        style={styles.bookButton}
        onClick={handleBooking}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        Book Appointment
      </motion.button>

      {showForm && (
        <motion.div
          style={styles.formContainer}
          initial={{ y: "100vh", opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          exit={{ y: "100vh", opacity: 0 }}
          transition={{ duration: 0.7 }}
        >
          <h2 style={styles.formTitle}>Enter Your Details</h2>
          <form onSubmit={handleFormSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              style={styles.input}
              required
            />
            <input
              type="number"
              name="age"
              placeholder="Age"
              style={styles.input}
              required
            />
            <select name="sex" style={styles.input} required>
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Transgender">Transgender</option>
            </select>
            <input
              type="text"
              name="phone"
              placeholder="Phone Number"
              style={styles.input}
              required
            />
            <textarea
              name="address"
              placeholder="Address"
              style={styles.input}
              required
            ></textarea>
            <motion.button
              type="submit"
              style={styles.submitButton}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              Submit
            </motion.button>
          </form>
        </motion.div>
      )}

      {bookingMessage && (
        <motion.div
          style={styles.alert}
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.3 }}
        >
          <p>{bookingMessage}</p>
        </motion.div>
      )}
    </motion.div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial, sans-serif",
    lineHeight: "1.6",
    color: "#333",
  },
  notFound: {
    textAlign: "center",
    marginTop: "50px",
  },
  backButton: {
    backgroundColor: "#f0f0f0",
    padding: "12px 20px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    marginBottom: "20px",
  },
  title: {
    fontSize: "36px",
    fontWeight: "bold",
    marginBottom: "15px",
    color: "#007BFF",
  },
  description: {
    fontSize: "18px",
    marginBottom: "30px",
    color: "#555",
  },
  subHeading: {
    fontSize: "24px",
    marginBottom: "15px",
    color: "#333",
  },
  treatmentList: {
    paddingLeft: "20px",
  },
  listItem: {
    marginBottom: "8px",
    color: "#444",
  },
  doctorContainer: {
    display: "flex",
    flexWrap: "wrap",
    gap: "25px",
    marginBottom: "25px",
  },
  doctorCard: {
    border: "1px solid #ddd",
    borderRadius: "10px",
    padding: "20px",
    width: "30%",
    textAlign: "center",
    transition: "transform 0.3s ease",
    backgroundColor: "#f9f9f9",
  },
  doctorName: {
    fontSize: "22px",
    fontWeight: "bold",
    marginBottom: "10px",
    color: "#333",
  },
  bookButton: {
    backgroundColor: "#007BFF",
    color: "#fff",
    padding: "12px 25px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    fontSize: "18px",
    marginBottom: "20px",
  },
  formContainer: {
    backgroundColor: "#f4f4f4",
    padding: "20px",
    borderRadius: "10px",
    maxWidth: "500px",
    margin: "0 auto",
    marginTop: "20px",
    boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
  },
  formTitle: {
    fontSize: "22px",
    fontWeight: "bold",
    marginBottom: "15px",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "8px 0",
    borderRadius: "5px",
    border: "1px solid #ddd",
  },
  submitButton: {
    backgroundColor: "#28a745",
    color: "#fff",
    padding: "10px 20px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    width: "100%",
  },
  alert: {
    backgroundColor: "#28a745",
    color: "#fff",
    padding: "15px",
    marginTop: "20px",
    borderRadius: "5px",
    textAlign: "center",
  },
};

export default DiseaseDetail;
