import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { ambulanceData } from './Ambulancedata';
import './AmbulanceDetailsPage.css';

const AmbulanceDetailsPage = () => {
  const { ambulanceId } = useParams();
  const ambulance = ambulanceData.find((a) => a.id === parseInt(ambulanceId));
  const [bookingSuccess, setBookingSuccess] = useState(false);
  const [showMap, setShowMap] = useState(false);
  const [destination, setDestination] = useState(null); // Store destination address
  const mapRef = useRef(null); // Reference to the map element

  useEffect(() => {
    if (showMap && destination) {
      if (window.google) {
        initMap();
      } else {
        // Wait for the Google Maps API to load if it's not ready yet
        window.addEventListener('google-map-script', initMap);
      }
    }
  }, [showMap, destination]);

  const initMap = () => {
    const directionsService = new google.maps.DirectionsService();
    const directionsRenderer = new google.maps.DirectionsRenderer();

    // Map initialization
    const map = new google.maps.Map(mapRef.current, {
      center: { lat: 12.9716, lng: 77.5946 }, // Default to Bangalore, change as needed
      zoom: 12,
    });
    directionsRenderer.setMap(map);

    // Dummy ambulance starting location
    const ambulanceStart = new google.maps.LatLng(12.9716, 77.5946);

    // Destination location based on user input (from form)
    const destinationLatLng = new google.maps.LatLng(destination.lat, destination.lng);

    // Create marker for ambulance location
    const ambulanceMarker = new google.maps.Marker({
      position: ambulanceStart,
      map: map,
      title: 'Ambulance Location',
      icon: 'http://maps.google.com/mapfiles/ms/icons/red-dot.png', // Change this to any icon URL
    });

    // Request directions to the destination
    directionsService.route(
      {
        origin: ambulanceStart,
        destination: destinationLatLng,
        travelMode: google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          directionsRenderer.setDirections(result);
        } else {
          alert('Directions request failed due to ' + status);
        }
      }
    );

    // Simulate ambulance moving (use some interval here)
    let currentPosition = ambulanceStart;
    let interval = setInterval(() => {
      if (currentPosition.lat() < destinationLatLng.lat()) {
        currentPosition = new google.maps.LatLng(
          currentPosition.lat() + 0.0001,
          currentPosition.lng()
        );
        ambulanceMarker.setPosition(currentPosition);
      } else {
        clearInterval(interval); // Stop moving once the destination is reached
      }
    }, 1000); // Update position every second
  };

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    setBookingSuccess(true);
    setTimeout(() => {
      setBookingSuccess(false);
    }, 3000); // Show alert for 3 seconds
  };

  const handleTrackButtonClick = () => {
    // Simulating destination from user input (this could come from form fields)
    setDestination({ lat: 12.9716, lng: 77.6356 }); // Example coordinates for destination
    setShowMap(true); // Show map on track button click
  };

  if (!ambulance) {
    return <h1>Ambulance not found!</h1>;
  }

  return (
    <div className="ambulance-details-page">
      <h1 className="ambulance-name">{ambulance.name}</h1>

      {/* Ambulance Images */}
      <div className="ambulance-images">
        <img
          src={ambulance.image1}
          alt={`${ambulance.name} view 1`}
          className="ambulance-image"
        />
        <img
          src={ambulance.image2}
          alt={`${ambulance.name} view 2`}
          className="ambulance-image"
        />
      </div>

      {/* Ambulance Info */}
      <p className="ambulance-contact">
        <strong>Contact:</strong> {ambulance.contact}
      </p>
      <p className="ambulance-location">
        <strong>Location:</strong> {ambulance.location}
      </p>

      <h2 className="section-heading">Facilities</h2>
      <ul className="ambulance-facilities">
        <li>24/7 Availability</li>
        <li>Emergency Medical Assistance</li>
        <li>Equipped with Oxygen Cylinders</li>
        <li>First Aid Kits</li>
        <li>Basic and Advanced Life Support</li>
      </ul>

      {/* Booking Form */}
      <h2 className="section-heading">Book Ambulance</h2>
      <form
        className="booking-form"
        onSubmit={handleBookingSubmit}
      >
        <label>
          Name:
          <input type="text" required />
        </label>
        <label>
          Contact:
          <input type="text" required />
        </label>
        <label>
          Address:
          <input type="text" required />
        </label>
        <button type="submit">Submit</button>

        {/* Track Ambulance Location Button */}
        <button
          type="button"
          className="track-location-button"
          onClick={handleTrackButtonClick} // Trigger map and marker movement
        >
          Track Ambulance
        </button>
      </form>

      {/* Map Preview */}
      {showMap && (
        <div className="map-preview">
          <h3>Ambulance Location</h3>
          <div ref={mapRef} className="map-container" style={{ width: '100%', height: '300px' }}></div>
        </div>
      )}

      {/* Success Alert */}
      {bookingSuccess && (
        <div className="alert">Ambulance service request submitted!</div>
      )}
    </div>
  );
};

export default AmbulanceDetailsPage;
