const DiseaseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const disease = diseaseDetails.find((d) => d.id === parseInt(id));

  const [showForm, setShowForm] = useState(false);
  const [bookingMessage, setBookingMessage] = useState("");

  if (!disease) {
    return (
      <div style={styles.notFound}>
        <h1>Disease not found</h1>
        <motion.button
          style={styles.backButton}
          onClick={() => navigate(-1)}
          whileHover={{ scale: 1.1 }}
        >
          Go Back
        </motion.button>
      </div>
    );
  }

  const handleBooking = () => setShowForm(true);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const appointmentNumber = Math.floor(1000 + Math.random() * 9000);
    setBookingMessage(
      `Appointment booked successfully! Your appointment number is ${appointmentNumber}.`
    );
    setShowForm(false);
  };

  return (
    <motion.div
      style={styles.container}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.7 }}
    >
      <motion.button
        style={styles.backButton}
        onClick={() => navigate(-1)}
        whileHover={{ scale: 1.1 }}
      >
        ← Back
      </motion.button>
      <motion.h1 style={styles.title}>{disease.name}</motion.h1>
      <motion.p style={styles.description}>{disease.description}</motion.p>
      <motion.h2 style={styles.subHeading}>Treatment Options:</motion.h2>
      <ul style={styles.treatmentList}>
        {disease.treatments.map((treatment, index) => (
          <li key={index} style={styles.listItem}>
            {treatment}
          </li>
        ))}
      </ul>
      <motion.h2 style={styles.subHeading}>Available Doctors:</motion.h2>
      <div style={styles.doctorContainer}>
        {disease.doctors.map((doctor, index) => (
          <motion.div key={index} style={styles.doctorCard}>
            <h3>{doctor.name}</h3>
            <p>Experience: {doctor.experience} years</p>
            <p>Fee: ₹{doctor.fee}</p>
          </motion.div>
        ))}
      </div>
      <motion.button
        style={styles.bookButton}
        onClick={handleBooking}
        whileHover={{ scale: 1.1 }}
      >
        Book Appointment
      </motion.button>
      {showForm && (
        <div style={styles.formContainer}>
          <h2>Enter Your Details</h2>
          <form onSubmit={handleFormSubmit}>
            <input type="text" placeholder="Full Name" required />
            <input type="number" placeholder="Age" required />
            <select required>
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Transgender">Transgender</option>
            </select>
            <input type="text" placeholder="Phone Number" required />
            <textarea placeholder="Address" required />
            <button type="submit">Submit</button>
          </form>
        </div>
      )}
      {bookingMessage && <div style={styles.alert}>{bookingMessage}</div>}
    </motion.div>
  );
};

const styles = {
  container: {
    padding: "20px",
    fontFamily: "Arial",
  },
  backButton: {
    backgroundColor: "#ddd",
    padding: "10px 15px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  title: { fontSize: "2rem", color: "#007BFF" },
  description: { fontSize: "1rem", color: "#555" },
  subHeading: { fontSize: "1.2rem", margin: "15px 0" },
  doctorContainer: { display: "flex", gap: "20px" },
  doctorCard: { border: "1px solid #ddd", padding: "15px", borderRadius: "10px" },
  bookButton: { backgroundColor: "#007BFF", color: "#fff", padding: "10px 15px" },
  formContainer: { padding: "20px", border: "1px solid #ddd", borderRadius: "10px" },
  alert: { color: "green", marginTop: "20px" },
};

export default DiseaseDetail;
