const DiseaseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const disease = diseaseDetails.find((d) => d.id === parseInt(id));

  const [showForm, setShowForm] = useState(false);
  const [bookingMessage, setBookingMessage] = useState("");

  if (!disease) {
    return (
      <div style={styles.notFound}>
        <h1>Disease not found</h1>
        <button style={styles.backButton} onClick={() => navigate(-1)}>
          Go Back
        </button>
      </div>
    );
  }

  const handleBooking = () => {
    setShowForm(true);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const appointmentNumber = Math.floor(1000 + Math.random() * 9000);
    setBookingMessage(
      `Appointment booked successfully! Your appointment number is ${appointmentNumber}.`
    );
    setShowForm(false);
  };

  return (
    <div style={styles.container}>
      <button style={styles.backButton} onClick={() => navigate(-1)}>
        ← Back
      </button>
      <h1 style={styles.title}>{disease.name}</h1>
      <p style={styles.description}>{disease.description}</p>
      <h2 style={styles.subHeading}>Treatment Options:</h2>
      <ul>
        {disease.treatments.map((treatment, index) => (
          <li key={index} style={styles.listItem}>
            {treatment}
          </li>
        ))}
      </ul>
      <h2 style={styles.subHeading}>Available Doctors:</h2>
      <div style={styles.doctorContainer}>
        {disease.doctors.map((doctor, index) => (
          <div key={index} style={styles.doctorCard}>
            <h3 style={styles.doctorName}>{doctor.name}</h3>
            <p>Experience: {doctor.experience} years</p>
            <p>Consultation Fee: ₹{doctor.fee}</p>
          </div>
        ))}
      </div>
      <button style={styles.bookButton} onClick={handleBooking}>
        Book Appointment
      </button>

      {showForm && (
        <div style={styles.formContainer} className="fade-in">
          <h2 style={styles.formTitle}>Enter Your Details</h2>
          <form onSubmit={handleFormSubmit}>
            <input type="text" name="name" placeholder="Full Name" style={styles.input} required />
            <input type="number" name="age" placeholder="Age" style={styles.input} required />
            <select name="sex" style={styles.input} required>
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Transgender">Transgender</option>
            </select>
            <input type="text" name="phone" placeholder="Phone Number" style={styles.input} required />
            <textarea name="address" placeholder="Address" style={styles.input} required></textarea>
            <button type="submit" style={styles.submitButton}>
              Submit
            </button>
          </form>
        </div>
      )}

      {bookingMessage && (
        <div style={styles.alert} className="fade-in">
          <p>{bookingMessage}</p>
        </div>
      )}
    </div>
  );
};

