import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./HomePage.css"; // External CSS file

const hospitals = [
  { name: "City Hospital", id: "city-hospital" },
  { name: "Green Valley Hospital", id: "green-valley-hospital" },
];

const doctors = [
  { name: "Dr. John Doe", id: "dr-john-doe" },
  { name: "Dr. Sarah Smith", id: "dr-sarah-smith" },
];

const HomePage = () => {
  const [query, setQuery] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSearch = () => {
    const foundHospital = hospitals.find(
      (hospital) => hospital.name.toLowerCase() === query.toLowerCase()
    );
    const foundDoctor = doctors.find(
      (doctor) => doctor.name.toLowerCase() === query.toLowerCase()
    );

    if (foundHospital) {
      navigate(`/hospital/${foundHospital.id}`);
    } else if (foundDoctor) {
      navigate(`/doctor/${foundDoctor.id}`);
    } else {
      setMessage("We don't have this hospital or doctor in our system.");
    }
  };

  return (
    <div className="home-page">
      <div className="card">
        <h2>Search for Hospitals or Doctors</h2>
        <p>Find the best healthcare services near you.</p>
      </div>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search hospitals or doctors..."
          value={query}
          onChange={(e) => {
            setQuery(e.target.value);
            setMessage("");
          }}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default HomePage;
