import React from "react";
import { useNavigate } from "react-router-dom";

const diseases = [
  { id: 1, name: "Diabetes", img: "/images/diabetes.jpg" },
  { id: 2, name: "Hypertension", img: "/images/hypertension.jpg" },
  { id: 3, name: "Asthma", img: "/images/asthma.jpg" },
  { id: 4, name: "Heart Disease", img: "/images/heart_disease.jpg" },
  { id: 5, name: "Arthritis", img: "/images/arthritis.jpg" },
  { id: 6, name: "Migraine", img: "/images/migraine.jpg" },
  { id: 7, name: "Allergies", img: "/images/allergies.jpg" },
  { id: 8, name: "Chronic Pain", img: "/images/chronic_pain.jpg" },
];

const DiseaseCards = () => {
  const navigate = useNavigate();

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 p-6">
      {diseases.map((disease) => (
        <div
          key={disease.id}
          className="group relative overflow-hidden bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 rounded-lg shadow-lg transform transition duration-500 hover:scale-105 cursor-pointer"
          onClick={() => navigate(`/disease/${disease.id}`)}
        >
          {/* Image */}
          <img
            src={disease.img}
            alt={disease.name}
            className="w-full h-40 object-cover rounded-t-lg group-hover:scale-110 transition duration-500"
          />
          {/* Name */}
          <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition duration-500 flex items-center justify-center">
            <h3 className="text-xl font-bold text-white">{disease.name}</h3>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DiseaseCards;
