import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { ambulanceData } from './Ambulancedata'; // Replace with actual ambulance data
import './AmbulanceDetailsPage.css';

const AmbulanceDetailsPage = () => {
  const { ambulanceId } = useParams();
  const ambulance = ambulanceData.find(a => a.id === parseInt(ambulanceId));

  const [bookingAlert, setBookingAlert] = useState(false);

  if (!ambulance) {
    return <h1>Ambulance not found</h1>;
  }

  const handleBookingSubmit = () => {
    setBookingAlert(true);
    setTimeout(() => {
      setBookingAlert(false);
    }, 3000); // Alert will display for 3 seconds
  };

  return (
    <div className="ambulance-details-page">
      {bookingAlert && <div className="booking-alert">Ambulance booked successfully!</div>}

      <h1 className="ambulance-name">{ambulance.name}</h1>

      {/* Ambulance Images */}
      <div className="ambulance-images">
        <img
          src={ambulance.image1}
          alt={`${ambulance.name} view 1`}
          className="ambulance-image"
        />
        <img
          src={ambulance.image2}
          alt={`${ambulance.name} view 2`}
          className="ambulance-image"
        />
      </div>

      <p className="ambulance-info"><strong>Details:</strong> {ambulance.description}</p>

      <h2 className="section-heading">Facilities:</h2>
      <ul className="facilities-list">
        {ambulance.facilities.map((facility, index) => (
          <li key={index}>{facility}</li>
        ))}
      </ul>

      <h2 className="section-heading">Book Ambulance Service</h2>
      <form
        className="booking-form"
        onSubmit={(e) => {
          e.preventDefault();
          handleBookingSubmit();
        }}
      >
        <label>
          Name:
          <input type="text" required />
        </label>
        <label>
          Contact:
          <input type="text" required />
        </label>
        <button type="submit">Book Now</button>
      </form>
    </div>
  );
};

export default AmbulanceDetailsPage;
