// HomePage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { hospitalData } from './data';
import './HomePage.css';

const HomePage = () => {
  const [search, setSearch] = useState('');

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  // const filteredHospitals = hospitalData.filter((hospital) =>
  //   hospital.name.toLowerCase().includes(search.toLowerCase())
  // );

  return (
    <div className="home-page">
      <div className="search-section">
        <h1>Find Hospitals & Doctors</h1>
        <div className="search-container">
          <select className="search-bar" value={search} onChange={handleSearchChange}>
            <option value="">Select Hospital</option>
            {hospitalData.map((hospital) => (
              <option key={hospital.id} value={hospital.name}>
                {hospital.name}
              </option>
            ))}
          </select>
          <button className="search-button">Search</button>
        </div>
      </div>

      <div className="card-container">
        <div className="card">
          <img src="hospital-placeholder.jpg" alt="Search for hospitals" className="hospital-img" />
          <p>Search for hospitals from our list</p>
        </div>
      </div>

      <div className="hospital-list">
        {/* {filteredHospitals.length > 0 && (
          <ul>
            {filteredHospitals.map((hospital) => ( */}
              <li key={hospital.id}>
                <Link to={`/details/${hospital.id}`}>{hospital.name}</Link>
              </li>
            {/* ))} */}
          {/* </ul> */}
        {/* )} */}
      </div>
    </div>
  );
};

export default HomePage;
