import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

// Mock data for 6 disease details
const diseaseDetails = [
  {
    id: 1,
    name: "Diabetes",
    description:
      "Diabetes is a chronic condition that affects how your body processes blood sugar (glucose). Common treatments include insulin therapy, medication, and lifestyle changes.",
    treatments: [
      "Insulin therapy",
      "Oral medication",
      "Dietary modifications",
      "Regular exercise",
    ],
    doctors: [
      { name: "Dr. John Doe", experience: 10, fee: 500 },
      { name: "Dr. Sarah Smith", experience: 8, fee: 400 },
      { name: "Dr. Emily Johnson", experience: 12, fee: 600 },
    ],
  },
  {
    id: 2,
    name: "Hypertension",
    description:
      "Hypertension, or high blood pressure, increases the risk of heart disease. Treatment includes medication, diet changes, and regular exercise.",
    treatments: [
      "Medication",
      "Low-sodium diet",
      "Weight management",
      "Regular physical activity",
    ],
    doctors: [
      { name: "Dr. Alice Brown", experience: 9, fee: 450 },
      { name: "Dr. Mark Wilson", experience: 11, fee: 550 },
      { name: "Dr. Sophia Miller", experience: 7, fee: 400 },
    ],
  },
  {
    id: 3,
    name: "Asthma",
    description:
      "Asthma is a condition in which your airways narrow and swell, causing difficulty breathing. Treatment includes inhalers and lifestyle adjustments.",
    treatments: [
      "Inhalers",
      "Steroid medication",
      "Breathing exercises",
      "Avoiding triggers",
    ],
    doctors: [
      { name: "Dr. James Taylor", experience: 15, fee: 500 },
      { name: "Dr. Natalie Evans", experience: 10, fee: 450 },
      { name: "Dr. Linda Clark", experience: 8, fee: 400 },
    ],
  },
  {
    id: 4,
    name: "Cancer",
    description:
      "Cancer is a group of diseases involving abnormal cell growth. Treatment varies depending on the type of cancer but may include chemotherapy, radiation, and surgery.",
    treatments: [
      "Chemotherapy",
      "Radiation therapy",
      "Surgery",
      "Targeted therapy",
    ],
    doctors: [
      { name: "Dr. Robert White", experience: 20, fee: 1200 },
      { name: "Dr. Patricia Lee", experience: 18, fee: 1500 },
      { name: "Dr. Michael Harris", experience: 25, fee: 1800 },
    ],
  },
  {
    id: 5,
    name: "Flu",
    description:
      "The flu is a contagious respiratory illness caused by influenza viruses. Treatment involves rest, hydration, and antiviral medication if necessary.",
    treatments: [
      "Rest",
      "Hydration",
      "Antiviral medication",
      "Pain relievers",
    ],
    doctors: [
      { name: "Dr. John Green", experience: 12, fee: 400 },
      { name: "Dr. Emily White", experience: 9, fee: 350 },
      { name: "Dr. Lisa Brown", experience: 5, fee: 300 },
    ],
  },
  {
    id: 6,
    name: "Arthritis",
    description:
      "Arthritis involves inflammation of the joints, leading to pain and stiffness. Treatment includes medication, physical therapy, and lifestyle changes.",
    treatments: [
      "Pain relievers",
      "Physical therapy",
      "Anti-inflammatory medications",
      "Exercise",
    ],
    doctors: [
      { name: "Dr. William Adams", experience: 14, fee: 600 },
      { name: "Dr. Olivia Davis", experience: 10, fee: 500 },
      { name: "Dr. Benjamin Walker", experience: 8, fee: 450 },
    ],
  },
];

const DiseaseDetail = () => {
  const { id } = useParams(); // Fetch disease id from URL
  const navigate = useNavigate();
  const disease = diseaseDetails.find((d) => d.id === parseInt(id));

  const [showForm, setShowForm] = useState(false);
  const [bookingMessage, setBookingMessage] = useState("");

  if (!disease) {
    return (
      <div className="text-center mt-20">
        <h1 className="text-2xl font-bold">Disease not found</h1>
        <button
          onClick={() => navigate(-1)}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
        >
          Go Back
        </button>
      </div>
    );
  }

  const handleBookingClick = () => {
    setShowForm(true); // Show the form when the button is clicked
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const name = formData.get("name");
    const age = formData.get("age");
    const sex = formData.get("sex");
    const phone = formData.get("phone");
    const address = formData.get("address");

    // Generate appointment number
    const appointmentNumber = Math.floor(1000 + Math.random() * 9000);

    // Update booking message
    setBookingMessage(
      `Appointment booked successfully! Your appointment number is ${appointmentNumber}.`
    );

    // Optionally, you can handle submitting this data to a backend here.
    setShowForm(false); // Hide the form after submission
  };

  return (
    <div className="p-6">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 bg-gray-200 px-4 py-2 rounded"
      >
        ← Back
      </button>
      <h1 className="text-3xl font-bold mb-4">{disease.name}</h1>
      <p className="text-lg mb-6">{disease.description}</p>
      <h2 className="text-2xl font-semibold mb-3">Treatment Options:</h2>
      <ul className="list-disc list-inside mb-6">
        {disease.treatments.map((treatment, index) => (
          <li key={index}>{treatment}</li>
        ))}
      </ul>
      <h2 className="text-2xl font-semibold mb-3">Available Doctors:</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-6">
        {disease.doctors.map((doctor, index) => (
          <div
            key={index}
            className="p-4 border rounded-lg shadow hover:shadow-lg"
          >
            <h3 className="text-xl font-bold">{doctor.name}</h3>
            <p>Experience: {doctor.experience} years</p>
            <p>Consultation Fee: ₹{doctor.fee}</p>
          </div>
        ))}
      </div>

      <button
        onClick={handleBookingClick}
        className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
      >
        Book Appointment
      </button>

      {/* Show form only when the button is clicked */}
      {showForm && (
        <form onSubmit={handleFormSubmit} className="mt-6 p-4 border rounded-lg">
          <h3 className="text-2xl font-bold mb-4">Fill Appointment Details</h3>
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            required
            className="mb-4 w-full p-2 border"
          />
          <input
            type="number"
            name="age"
            placeholder="Age"
            required
            className="mb-4 w-full p-2 border"
          />
          <select name="sex" className="mb-4 w-full p-2 border">
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Transgender">Transgender</option>
          </select>
          <input
            type="text"
            name="phone"
            placeholder="Phone Number"
            required
            className="mb-4 w-full p-2 border"
          />
          <textarea
            name="address"
            placeholder="Address"
            required
            className="mb-4 w-full p-2 border"
          ></textarea>
          <button
            type="submit"
            className="bg-green-500 text-white px-6 py-2 rounded hover:bg-green-600"
          >
            Submit
          </button>
        </form>
      )}

      {/* Display the booking message directly on the webpage */}
      {bookingMessage && (
        <div className="mt-6 p-4 bg-green-100 text-green-700 rounded">
          <p>{bookingMessage}</p>
        </div>
      )}
    </div>
  );
};

export default DiseaseDetail;
