import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { hospitalData } from './data';
import './HomePage.css';

const HomePage = () => {
  const [search, setSearch] = useState('');
  const [filteredHospitals, setFilteredHospitals] = useState(hospitalData);
  const navigate = useNavigate();

  const handleSearchChange = (e) => {
    const searchTerm = e.target.value.toLowerCase();
    setSearch(searchTerm);

    // Filter hospitals based on the search term
    const filtered = hospitalData.filter((hospital) =>
      hospital.name.toLowerCase().includes(searchTerm)
    );
    setFilteredHospitals(filtered);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    const selectedHospital = hospitalData.find(
      (hospital) => hospital.name.toLowerCase() === search.toLowerCase()
    );

    if (selectedHospital) {
      // Redirect to the DetailsPage with the hospital ID
      navigate(`/details/${selectedHospital.id}`);
    } else {
      alert('Hospital not found. Please try again.');
    }
  };

  return (
    <div className="home-page">
      <div className="card-container">
        <div className="card">
          <img src="/images/hospital.jpg" alt="Search for hospitals" className="hospital-img" />
          <p>Search for hospitals from our list</p>
        </div>
      </div>

      <div className="search-section">
        <div className="scroll-text">
          <span>Find Hospitals & Doctors</span>
        </div>
        <form className="search-container" onSubmit={handleSearchSubmit}>
          {/* Input field for search */}
          <input
            type="text"
            className="search-bar"
            placeholder="Type to search hospitals..."
            value={search}
            onChange={handleSearchChange}
          />

          {/* Filterable dropdown for hospitals */}
          <select
            className="search-bar"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          >
            <option value="">Select Hospital</option>
            {filteredHospitals.map((hospital) => (
              <option key={hospital.id} value={hospital.name}>
                {hospital.name}
              </option>
            ))}
          </select>

          <button type="submit" className="search-button">
            Search
          </button>
        </form>
      </div>
    </div>
  );
};

export default HomePage;
